var glossary=new Array;$(document).ready(function(){function e(e){$(e).find("term").each(function(){var e={};e.id=$(this).attr("id");e.label=$(this).find("label").text();e.desc=jQuery.trim($(this).find("fullDescription").text());glossary[e.id]=e});$("span[termId]").each(function(){var e=glossary[$(this).attr("termId")];$(this).qtip({hide:{delay:500,fixed:true},content:{text:e.desc,title:{text:e.label}},position:{my:"bottom left",at:"top right"}})})}$("#help").dialog({autoOpen:false,closeOnEscape:true,resizable:false,modal:false,buttons:{}});$.ajax({type:"GET",url:"/glossary/glossary.xml",dataType:"xml",success:e})})